import sys
import time
import random
import os
import base64
from PyQt5.QtWidgets import (
    QApplication, QMainWindow, QPushButton, QVBoxLayout,
    QWidget, QLabel, QLineEdit, QSpinBox, QFileDialog, QCheckBox,
    QTextEdit
)
from PyQt5.QtGui import QPixmap, QIcon
from PyQt5.QtCore import Qt, QThread, pyqtSignal
from selenium import webdriver
from selenium.webdriver.chrome.service import Service
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC

# Base64 encoded icon (Replace this with your own Base64 string)
ICON_BASE64 = """


"""

class CPAWorker(QThread):
    status_update = pyqtSignal(str)
    proxy_update = pyqtSignal(str)
    stats_update = pyqtSignal(str)
    error_signal = pyqtSignal(str)

    def __init__(self, website_url, proxy_list, click_limit, sleep_time, ad_selector):
        super().__init__()
        self.website_url = website_url
        self.proxy_list = proxy_list
        self.click_limit = click_limit
        self.sleep_time = sleep_time
        self.ad_selector = ad_selector
        self.page_visits = 0
        self.successful_clicks = 0

    def run(self):
        driver_path = os.path.join(os.path.dirname(__file__), 'ChromeDriver114', 'chromedriver.exe')
        chrome_binary = os.path.join(os.path.dirname(__file__), 'ChromePortable114', 'ChromePortable.exe')

        options = Options()
        options.add_argument("--log-level=3")
        options.binary_location = chrome_binary
        service = Service(driver_path)

        used_proxies = set()

        for i in range(self.click_limit):
            # Select a random proxy
            available_proxies = [p for p in self.proxy_list if p not in used_proxies]
            if not available_proxies:
                self.error_signal.emit('No more available proxies to use.')
                break

            proxy = random.choice(available_proxies)
            used_proxies.add(proxy)

            self.proxy_update.emit(f'Using proxy: {proxy}')
            selenium_proxy = webdriver.Proxy()
            selenium_proxy.proxy_type = webdriver.common.proxy.ProxyType.MANUAL
            selenium_proxy.http_proxy = proxy
            selenium_proxy.ssl_proxy = proxy

            capabilities = selenium_proxy.to_capabilities()
            capabilities.update(webdriver.DesiredCapabilities.CHROME.copy())

            driver = webdriver.Chrome(service=service, options=options)

            try:
                driver.get(self.website_url)
                self.page_visits += 1
                self.wait_for_page_load(driver)
                self.scroll_to_bottom(driver)
                self.wait_for_ads(driver)
                self.click_random_ads(driver)
                
                # Emit periodic status and stats updates
                self.update_stats()

            except Exception as e:
                self.error_signal.emit(f'Error: {e}')
            finally:
                driver.quit()

        self.status_update.emit('CPA process completed.')

    def update_stats(self):
        click_to_visit_ratio = (self.successful_clicks / self.page_visits) if self.page_visits > 0 else 0
        self.stats_update.emit(
            f'Page Visits: {self.page_visits}\n'
            f'Successful Clicks: {self.successful_clicks}\n'
            f'Click-to-Visit Ratio: {click_to_visit_ratio:.2f}'
        )

    def wait_for_page_load(self, driver, timeout=10):
        try:
            WebDriverWait(driver, timeout).until(
                EC.presence_of_element_located((By.TAG_NAME, 'body'))
            )
            self.status_update.emit('Page loaded.')
        except Exception as e:
            self.error_signal.emit(f'Error waiting for page to load: {e}')

    def scroll_to_bottom(self, driver):
        driver.execute_script("window.scrollTo(0, document.body.scrollHeight);")
        time.sleep(self.sleep_time)  # Use the sleep time variable

    def wait_for_ads(self, driver, timeout=10):
        try:
            WebDriverWait(driver, timeout).until(
                EC.presence_of_element_located((By.CSS_SELECTOR, self.ad_selector))
            )
            self.status_update.emit('Ads are present.')
        except Exception as e:
            self.error_signal.emit(f'Error waiting for ads: {e}')

    def click_random_ads(self, driver):
        try:
            ad_elements = driver.find_elements(By.CSS_SELECTOR, f'{self.ad_selector} *')
            if not ad_elements:
                self.status_update.emit('No ad elements found.')
                return

            ads_to_click = random.sample(ad_elements, min(len(ad_elements), 4))
            
            for i, ad in enumerate(ads_to_click):
                if i >= self.click_limit:
                    break
                try:
                    driver.execute_script("arguments[0].scrollIntoView(true);", ad)
                    WebDriverWait(driver, 10).until(
                        EC.element_to_be_clickable(ad)
                    ).click()
                    self.successful_clicks += 1
                    self.status_update.emit(f'Clicked on ad {i+1}.')
                    time.sleep(self.sleep_time)  # Use configurable sleep time
                    self.update_stats()  # Update stats after each click
                except Exception as e:
                    self.error_signal.emit(f'Failed to click ad {i+1}: {e}')
        except Exception as e:
            self.error_signal.emit(f'Error clicking ads: {e}')


class CPAAutomation(QMainWindow):
    def __init__(self):
        super().__init__()
        self.initUI()

    def initUI(self):
        self.setWindowTitle('CPA Automation Tool')
        self.setFixedSize(600, 500)  # Set fixed size

        # Set the Base64 icon
        icon_data = base64.b64decode(ICON_BASE64)
        icon_pixmap = QPixmap()
        icon_pixmap.loadFromData(icon_data)
        self.setWindowIcon(QIcon(icon_pixmap))

        layout = QVBoxLayout()

        self.websiteLabel = QLabel('Website URL:')
        layout.addWidget(self.websiteLabel)

        self.websiteInput = QLineEdit(self)
        layout.addWidget(self.websiteInput)

        self.proxyLabel = QLabel('Proxy List File:')
        layout.addWidget(self.proxyLabel)

        self.proxyInput = QLineEdit(self)
        self.proxyInput.setReadOnly(True)
        layout.addWidget(self.proxyInput)

        self.browseProxyBtn = QPushButton('Browse...', self)
        self.browseProxyBtn.clicked.connect(self.load_proxy_file)
        layout.addWidget(self.browseProxyBtn)

        self.clickLimitLabel = QLabel('Click Limit:')
        layout.addWidget(self.clickLimitLabel)

        self.clickLimitSpin = QSpinBox(self)
        self.clickLimitSpin.setRange(1, 10000)  # Set to 1-10,000
        layout.addWidget(self.clickLimitSpin)

        self.sleepTimeLabel = QLabel('Sleep Time (seconds):')
        layout.addWidget(self.sleepTimeLabel)

        self.sleepTimeSpin = QSpinBox(self)
        self.sleepTimeSpin.setRange(1, 60)
        layout.addWidget(self.sleepTimeSpin)

        self.adSelectorLabel = QLabel('Ad CSS Selector:')
        layout.addWidget(self.adSelectorLabel)

        self.adSelectorInput = QLineEdit(self)
        self.adSelectorInput.setText('#container-cf01a52a9a06f1b0dbe08c1ea157aba1')  # Default value
        layout.addWidget(self.adSelectorInput)

        self.startBtn = QPushButton('Start', self)
        self.startBtn.clicked.connect(self.start_cpa)
        layout.addWidget(self.startBtn)

        self.statusLabel = QLabel('')
        layout.addWidget(self.statusLabel)

        self.proxyStatusLabel = QLabel('No proxy in use.')
        layout.addWidget(self.proxyStatusLabel)

        self.statsLabel = QLabel('Page Visits: 0\nSuccessful Clicks: 0\nClick-to-Visit Ratio: 0.00')
        layout.addWidget(self.statsLabel)

        self.errorTextEdit = QTextEdit()
        self.errorTextEdit.setPlaceholderText('Error messages will be displayed here.')
        self.errorTextEdit.setReadOnly(True)
        layout.addWidget(self.errorTextEdit)

        container = QWidget()
        container.setLayout(layout)
        self.setCentralWidget(container)

    def load_proxy_file(self):
        options = QFileDialog.Options()
        fileName, _ = QFileDialog.getOpenFileName(self, "Load Proxy List", "", "Text Files (*.txt);;All Files (*)", options=options)
        if fileName:
            self.proxyInput.setText(fileName)

    def start_cpa(self):
        website_url = self.websiteInput.text()
        proxy_file = self.proxyInput.text()
        click_limit = self.clickLimitSpin.value()
        sleep_time = self.sleepTimeSpin.value()
        ad_selector = self.adSelectorInput.text()

        if not website_url or not proxy_file:
            self.statusLabel.setText('Please fill in all fields.')
            return

        try:
            with open(proxy_file, 'r') as file:
                proxies = [line.strip() for line in file.readlines()]
        except Exception as e:
            self.statusLabel.setText(f'Error loading proxy file: {e}')
            return

        self.worker = CPAWorker(website_url, proxies, click_limit, sleep_time, ad_selector)
        self.worker.status_update.connect(self.update_status)
        self.worker.proxy_update.connect(self.update_proxy_status)
        self.worker.stats_update.connect(self.update_stats)
        self.worker.error_signal.connect(self.update_error_display)
        self.worker.start()

    def update_status(self, message):
        self.statusLabel.setText(message)

    def update_proxy_status(self, proxy_message):
        self.proxyStatusLabel.setText(proxy_message)

    def update_stats(self, stats_message):
        self.statsLabel.setText(stats_message)

    def update_error_display(self, error_message):
        self.errorTextEdit.append(error_message)


if __name__ == '__main__':
    app = QApplication(sys.argv)
    ex = CPAAutomation()
    ex.show()
    sys.exit(app.exec_())
